sys_variable_value.value:
(function execute(inputs, outputs) {

var gr1 = inputs.sitecode;
var code_split = gr1.split(",");
for(var i=0; i < code_split.length; i++){
gs.info('Test1' +code_split[i]);
var gr = new GlideRecord("change_task");
 gr.addQuery('123TEXTQUERY321',code_split[i]);
//gs.info('Test' +gr.getEncodedQuery());
gr.query();

while(gr.next())
{
    outputs.id=gr.code_split[i];
   }

}
})(inputs, outputs);

